package vod_test

import (
	"testing"

	"github.com/hashicorp/terraform-plugin-sdk/v2/helper/resource"
	tcacctest "github.com/tencentcloudstack/terraform-provider-tencentcloud/tencentcloud/acctest"
)

const vodWatermarkTemplateKey = "tencentcloud_vod_watermark_template.watermark_template"

func TestAccTencentCloudVodWatermarkTemplateResource_basic(t *testing.T) {
	t.Parallel()
	resource.Test(t, resource.TestCase{
		PreCheck: func() {
			tcacctest.AccPreCheck(t)
		},
		Providers: tcacctest.AccProviders,
		Steps: []resource.TestStep{
			{
				Config: testAccVodWatermarkTemplate,
				Check: resource.ComposeTestCheckFunc(
					resource.TestCheckResourceAttrSet(vodWatermarkTemplateKey, "id"),
					resource.TestCheckResourceAttr(vodWatermarkTemplateKey, "name", "myImageWatermark"),
					resource.TestCheckResourceAttr(vodWatermarkTemplateKey, "comment", "a png watermark"),
					resource.TestCheckResourceAttr(vodWatermarkTemplateKey, "coordinate_origin", "TopLeft"),
					resource.TestCheckResourceAttr(vodWatermarkTemplateKey, "x_pos", "10%"),
					resource.TestCheckResourceAttr(vodWatermarkTemplateKey, "y_pos", "10%"),
					resource.TestCheckResourceAttrSet(vodWatermarkTemplateKey, "image_template.0.image_content"),
					resource.TestCheckResourceAttr(vodWatermarkTemplateKey, "image_template.0.width", "10%"),
					resource.TestCheckResourceAttr(vodWatermarkTemplateKey, "image_template.0.height", "10px"),
				),
			},
			{
				Config: testAccVodWatermarkTemplateUpdate,
				Check: resource.ComposeTestCheckFunc(
					resource.TestCheckResourceAttr(vodWatermarkTemplateKey, "comment", "a png watermark update"),
					resource.TestCheckResourceAttr(vodWatermarkTemplateKey, "x_pos", "11%"),
					resource.TestCheckResourceAttr(vodWatermarkTemplateKey, "y_pos", "12%"),
					resource.TestCheckResourceAttr(vodWatermarkTemplateKey, "image_template.0.width", "20%"),
					resource.TestCheckResourceAttr(vodWatermarkTemplateKey, "image_template.0.height", "20px"),
				),
			},
			{
				ResourceName:      "tencentcloud_vod_watermark_template.watermark_template",
				ImportState:       true,
				ImportStateVerify: true,
			},
		},
	})
}

const testAccVodWatermarkTemplate = `

resource  "tencentcloud_vod_sub_application" "watermark_template_sub_application" {
	name = "watermarkTemplateSubApplication"
	status = "On"
	description = "this is sub application"
}

resource "tencentcloud_vod_watermark_template" "watermark_template" {
	type = "image"
	sub_app_id = tonumber(split("#", tencentcloud_vod_sub_application.watermark_template_sub_application.id)[1])
	name = "myImageWatermark"
	comment = "a png watermark"
	coordinate_origin = "TopLeft"
	x_pos = "10%"
	y_pos = "10%"
	image_template {
		image_content = ""
		width = "10%"
		height = "10px"
	}
}
`

const testAccVodWatermarkTemplateUpdate = `

resource  "tencentcloud_vod_sub_application" "watermark_template_sub_application" {
	name = "watermarkTemplateSubApplication"
	status = "On"
	description = "this is sub application"
}

resource "tencentcloud_vod_watermark_template" "watermark_template" {
	type = "image"
	sub_app_id = tonumber(split("#", tencentcloud_vod_sub_application.watermark_template_sub_application.id)[1])
	name = "myImageWatermark"
	comment = "a png watermark update"
	coordinate_origin = "TopLeft"
	x_pos = "11%"
	y_pos = "12%"
	image_template {
		image_content = ""
		width = "20%"
		height = "20px"
	}
}
`
